package homeworkloops;

public class LoopsLevel1Task3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Tablica ymnojenia na 5:");
		for (int i = 1; i <= 9; i++) {
			int m = 5 * i;
			System.out.print(i + "*5=" + m + ", ");

		}
	}

}
