package methods;

public class MethodLevel1Task2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String a = "Numbers:";
		int b = 5;
		double c = 5.5;
		System.out.println(numberToString(a, b, c));

	}

	public static String numberToString(String a, int b, double c) {

		return a + " " + b + " and " + c;
	}

}
